import React from 'react';
import DatePicker from 'react-date-picker';
import './TimePreferences.css';
import { Dropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function TimePreferences () {

    const [startDate, setStartDate] = React.useState(new Date());
    const [endDate, setEndDate] = React.useState(new Date());
    const [checkedDays, setCheckedDays] = React.useState([false, false, false, false, false, false, false]);

    const updateDays = (idx) => {
        const newCheckedDays = [...checkedDays];
        if (newCheckedDays[idx]) {
            newCheckedDays[idx] = false;
        } else {
            newCheckedDays[idx] = true;
        }
        setCheckedDays(newCheckedDays);
    }

    return (
        <div id='time-preferences-container'>
            <h1>Time Preferences</h1>
            <div id='days-checklist'>
                {checkedDays.map((val, idx) => {
                    return (
                        <DayBox 
                            visible={checkedDays[idx]}
                            index={idx}
                            setDay={() => updateDays(idx)}
                        />
                    );
                })}
            </div>
            <div id='start-end-date'>
                <DatePicker value={startDate} onChange={setStartDate} />
                <DatePicker value={endDate} onChange={setEndDate} />
            </div>
            <div id='interval-config'>
                <Dropdown>
                    Let me take a break every
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        Dropdown Button
                    </Dropdown.Toggle>

                    <Dropdown.Menu>
                        <Dropdown.Item href="#/action-2">5 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">10 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-1">15 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-2">20 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">25 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-1">30 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-2">35 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">40 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-1">45 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-2">50 Minutes</Dropdown.Item>
                        <Dropdown.Item href="#/action-3">Hour</Dropdown.Item>

                    </Dropdown.Menu>
                </Dropdown>
            </div>
        </div>
    );
}

function DayBox ( {visible, index, setDay} ) {
    const dayArray = ['Sun', 'Mon', 'Tues', 'Wed', 'Thurs', 'Fri', 'Sat'];
    if (visible === true) {
        return (
            <span 
                className='day-box' 
                onClick={setDay}
                style={{backgroundColor: "red"}}>{dayArray[index]}
            </span> 
        );
    } else {
        return (
            <span 
                className='day-box' 
                onClick={setDay}
                style={{backgroundColor: "yellow"}}>{dayArray[index]}

            </span> 
        );
    }
}

export default TimePreferences;
