import React from 'react';

import './Preferences.css';

function Preferences () {

    const [youtuberInputs, setYoutuberInputs] = React.useState(['']);

    const updateYoutuber = (index, value) => {
        const newYoutuberInputs = [...youtuberInputs];
        newYoutuberInputs[index] = value;
        setYoutuberInputs(newYoutuberInputs);
    }

    const addYoutuber = () => {
        const newYoutuberInputs = [...youtuberInputs];
        newYoutuberInputs.push('');
        setYoutuberInputs(newYoutuberInputs);
    }

    return (
        <div id='preferences-container'>
            <div id='favourite-youtubers'>
                My Favourite Youtubers
                <div>
                    {youtuberInputs.map((val, idx) => {
                        return(
                            <input
                                className="youtuber"
                                key={idx}
                                index={idx}
                            />
                        );
                    })}
                </div>
                <button onClick={addYoutuber} id='add-youtuber'>Add YouTuber</button>
            </div>


        </div>
    );
}

export default Preferences;
