import React from 'react';
import DatePicker from 'react-date-picker';
import TimePicker from 'react-time-picker';
import './TimePreferences.css';
import { Dropdown } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const dayArray = ['Sun', 'Mon', 'Tues', 'Wed', 'Thurs', 'Fri', 'Sat'];

function TimePreferences () {

    const [startDate, setStartDate] = React.useState(new Date());
    const [endDate, setEndDate] = React.useState(new Date());
    const [checkedDays, setCheckedDays] = React.useState([false, false, false, false, false, false, false]);
    const [startTime, setStartTime] = React.useState('00:00');
    const [intervalTime, setIntervalTime] = React.useState('25');

    const updateDays = (idx) => {
        const newCheckedDays = [...checkedDays];
        if (newCheckedDays[idx]) {
            newCheckedDays[idx] = false;
        } else {
            newCheckedDays[idx] = true;
        }
        setCheckedDays(newCheckedDays);
    }

    React.useEffect(() => {
        console.log("intervalTime=", intervalTime);
    }, [intervalTime]);

    return (
        <div id='time-preferences-container'>
            <h1>Time Preferences</h1>
            <div id='days-checklist'>
                {checkedDays.map((val, idx) => {
                    return (
                        <DayBox 
                            visible={checkedDays[idx]}
                            index={idx}
                            setDay={() => updateDays(idx)}
                        />
                    );
                })}
            </div>
            <div id='start-end-date'>
                <DatePicker value={startDate} onChange={setStartDate} />
                <DatePicker value={endDate} onChange={setEndDate} />
                <TimePicker value={startTime} onChange={setStartTime} />
            </div>
            <div id='interval-config'>
                <Dropdown>
                    Let me take a break every
                    <Dropdown.Toggle variant="primary" id="dropdown-basic">
                        {intervalTime} minutes
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                        <Dropdown.Item onSelect={() => setIntervalTime(5)}>5 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(10)}>10 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(15)}>15 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(20)}>20 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(25)}>25 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(30)}>30 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(35)}>35 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(40)}>40 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(45)}>45 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(50)}>50 minutes</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(60)}>1 hour</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(90)}>1.5 hours</Dropdown.Item>
                        <Dropdown.Item onSelect={() => setIntervalTime(120)}>2 hours</Dropdown.Item>
                    </Dropdown.Menu>
                </Dropdown>
            </div>
        </div>
    );
}

function DayBox ( {visible, index, setDay} ) {
    
    if (visible === true) {
        return (
            <span
                className='day-box' 
                onClick={setDay}
                style={{backgroundColor: "red", cursor:"pointer"}}>{dayArray[index]}
            </span> 
        );
    } else {
        return (
            <span 
                className='day-box' 
                onClick={setDay}
                style={{backgroundColor: "yellow", cursor:"pointer"}}>{dayArray[index]}
            </span> 
        );
    }
}

export default TimePreferences;
